      },
      "otherInSituTests": {
        "attributes": {},
        "geometry": "3D Point or 3D LineString"
      },
      "sample": {
        "attributes": {},
        "geometry": "3D Point",
        "children": {
          "grainSizeDistribution": {},
          "atterbergLimits": {},
          "oedometerTest": {},
